function hidePlaces(){document.getElementById("activities").style.display="none",document.getElementById("showplaces").innerHTML="( + ) show me where to go!"}function showPlaces(){document.getElementById("activities").style.display="block",document.getElementById("showplaces").innerHTML="( - ) I've seen enough for now!"}function togglePlaces(){"block"==document.getElementById("activities").style.display?hidePlaces():showPlaces()}function hideFoods(){document.getElementById("dishes").style.display="none",document.getElementById("showfoods").innerHTML="( + ) show me the food!"}function showFoods(){document.getElementById("dishes").style.display="block",document.getElementById("showfoods").innerHTML="( - ) maybe later..."}function toggleFoods(){"block"==document.getElementById("dishes").style.display?hideFoods():showFoods()}window.onload=function(){hideFoods(),hidePlaces()};