function hideCaliPlaces(){document.getElementById("caliplaces").style.display="none",document.getElementById("showcaliplaces").innerHTML="( + ) show me where to go!"}function showCaliPlaces(){document.getElementById("caliplaces").style.display="block",document.getElementById("showcaliplaces").innerHTML="( - ) I've seen enough for now!"}function toggleCaliPlaces(){"block"==document.getElementById("caliplaces").style.display?hideCaliPlaces():showCaliPlaces()}function hideCaliFoods(){document.getElementById("califoods").style.display="none",document.getElementById("showcalifoods").innerHTML="( + ) show me the food!"}function showCaliFoods(){document.getElementById("califoods").style.display="block",document.getElementById("showcalifoods").innerHTML="( - ) maybe later..."}function toggleCaliFoods(){"block"==document.getElementById("califoods").style.display?hideCaliFoods():showCaliFoods()}window.onload=function(){hideCaliFoods(),hideCaliPlaces()};