function hideNolaPlaces(){document.getElementById("nolaplaces").style.display="none",document.getElementById("shownolaplaces").innerHTML="( + ) show me where to go!"}function showNolaPlaces(){document.getElementById("nolaplaces").style.display="block",document.getElementById("shownolaplaces").innerHTML="( - ) I've seen enough for now!"}function toggleNolaPlaces(){"block"==document.getElementById("nolaplaces").style.display?hideNolaPlaces():showNolaPlaces()}function hideNolaFoods(){document.getElementById("nolafoods").style.display="none",document.getElementById("shownolafoods").innerHTML="( + ) show me the food!"}function showNolaFoods(){document.getElementById("nolafoods").style.display="block",document.getElementById("shownolafoods").innerHTML="( - ) maybe later..."}function toggleNolaFoods(){"block"==document.getElementById("nolafoods").style.display?hideNolaFoods():showNolaFoods()}window.onload=function(){hideNolaFoods(),hideNolaPlaces()};
